    private void btnSalvarActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnSalvarActionPerformed
        Pessoa pessoa = new Pessoa(this.txtNome.getText(), this.txtCPF.getText(), this.txtTel.getText());  
        if (this.controle.testarVazio(pessoa)) {
              JOptionPane.showMessageDialog(null, "Por favor, digite todos os campos");
        }
        else{
              if (this.controle.salvar(pessoa)) {
                  this.limpar(); 
                  this.carregarTabela(this.controle.getListaPessoas()); 
                  JOptionPane.showMessageDialog(null, "Cliente adicionado com sucesso"); 
                  
              }
        }
    }//GEN-LAST:event_btnSalvarActionPerformed

    private void btnExcluirActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnExcluirActionPerformed
        int id = this.jTable1.getSelectedRow(); 
        this.controle.deletar(id); 
        this.carregarTabela(this.controle.getListaPessoas());  
           JOptionPane.showMessageDialog(null, "Cliente removido com sucesso"); 
    }//GEN-LAST:event_btnExcluirActionPerformed

