    private void btnExcluir1MouseClicked(java.awt.event.MouseEvent evt) {//GEN-FIRST:event_btnExcluir1MouseClicked
        int id = this.jTable1.getSelectedRow(); 
        this.controle.deletar(id); 
         if (id == -1) {
        JOptionPane.showMessageDialog(this, "Selecione uma linha para editar");
        return;
    }

    // Obtenha os dados da linha selecionada
    String nome = jTable1.getValueAt(id, 0).toString();
    String cpf = jTable1.getValueAt(id, 1).toString();
    String telefone = jTable1.getValueAt(id, 2).toString();

    // Preencha os campos de texto com os dados da linha selecionada
    txtNome.setText(nome);
    txtCPF.setText(cpf);
    txtTel.setText(telefone);
        
        
    }//GEN-LAST:event_btnExcluir1MouseClicked

    private void txtCPFKeyTyped(java.awt.event.KeyEvent evt) {//GEN-FIRST:event_txtCPFKeyTyped
       String caracteres = "0123456789"; 
       if (!caracteres.contains(evt.getKeyChar()+"")){
           evt.consume(); 
       }
    }//GEN-LAST:event_txtCPFKeyTyped

    private void txtNomeKeyTyped(java.awt.event.KeyEvent evt) {//GEN-FIRST:event_txtNomeKeyTyped
           String caracteres = "0123456789";   // caracteres de exemplo
       if (caracteres.contains(evt.getKeyChar()+"")){ // se esses caracteres forem iguais dos exemplos
           evt.consume(); //consumir ou seja, "apagar". 
       }
    }//GEN-LAST:event_txtNomeKeyTyped

    private void txtTelKeyTyped(java.awt.event.KeyEvent evt) {//GEN-FIRST:event_txtTelKeyTyped
           String caracteres = "0123456789"; 
       if (!caracteres.contains(evt.getKeyChar()+"")){ // se esses caracteres forem diferente dos exemplos
           evt.consume(); 
       }
    }//GEN-LAST:event_txtTelKeyTyped

            public void limpar(){
            this.txtNome.setText(""); 
            this.txtCPF.setText(""); 
            this.txtTel.setText(""); 
}
            
            
            
            public void carregarTabela(ArrayList<Pessoa>  listaPessoa) {
               this.model.setRowCount(0); 
               for(int i=0; i<listaPessoa.size();i++) {
                   this.model.insertRow(i,  new Object[]{listaPessoa.get(i).getNome(),listaPessoa.get(i).getCPF(),listaPessoa.get(i).getTelefone()}); 
               }
            }
   
    
